mapper.select:
select * from (select rownum rn,e.* from (SELECT bak.id,BAK.PROPOSER_NAME, BAK.ID_CARD_NO, BAK.MOBILEPHONE, BAK.RELIEVE_DATE, BAK.REASON, BAK.ADDTIME, BAK.ADDUSERID, BAK.UNIT_NAME,SYSU.DISPLAY_NAME FROM BASE_BLACKLIST bak LEFT JOIN SYS_USER sysu on SYSU.USER_ID=BAK.ADDUSERID where 1=1
and bak.PROPOSER_NAME like '%'||#{PROPOSER_NAME}||'%'
and bak.UNIT_NAME like '%'||#{UNIT_NAME}||'%'
) e
where rownum <=#{rows}*#{page})f where rn>(#{page}-1)*#{rows}

select  from BASE_BLACKLIST where ID_CARD_NO= #{idCardNo}
and  STATE = '1'
and SYSDATE < RELIEVE_DATE
