mapper.select:
select

from REC_EXAMRESULT WHERE EXAM_NAME = #{pd.examName}

  and EXAM_CLASS = #{pd.examClass}


  and YEAR_NO = #{pd.yearNo}


  and USER_NAME like '%'||#{pd.userName}||'%'

select

   from REC_EXAMRESULT WHERE 1=1

     and ID_CARD_NO = #{idCardNo,jdbcType=VARCHAR}


     and ID != #{id}

select 
from
(select rownum rn,a.* from
(select

from REC_EXAMRESULT WHERE EXAM_NAME = #{examName}

  and EXAM_CLASS = #{examClass}





  and YEAR_NO = #{yearNo}


  and USER_NAME like '%'||#{userName}||'%'

ORDER by addtime) a

  where rownum <=#{rows}*#{page}) b
  where  rn>(#{page}-1)*#{rows}

select count(*) as listcount from REC_EXAMRESULT  WHERE EXAM_NAME = #{examName}

  and EXAM_CLASS = #{examClass}





  and YEAR_NO = #{yearNo}


  and USER_NAME like '%'||#{userName}||'%'
