mapper.select:
select * from (select rownum rn,e.* from ( select t1.*,tb1.*,tb2.JUDGING_NAME,tb3.GROUP_NAME,tb4.UNIT_NAME,tb5.dict_name as SERIES_NAME,tb6.*,tb7.PROFESSIAL_NAME,tb8.DICT_NAME as REVIEW_TYPE_NAME from REC_REVIEWRESULTPUBLICITY t1 left join (select tab1.AREA_CODE as AREA_NUMBER,tab1.AREA_NAME,tab1.AREA_GRADE,tab2.AREA_NAME as pname,tab2.AREA_GRADE as pgrade from SYS_AREA tab1 left join sys_area tab2 on tab1.AREA_PARENTCODE = tab2.AREA_CODE) tb1 on t1.AREA_CODE = tb1.AREA_NUMBER left join BASE_JUDGING tb2 on t1.JUDGING_CODE = tb2.JUDGING_CODE left join BASE_SUBJECT_GROUP tb3 on tb3.id = t1.GROUP_ID left join BASE_UNIT tb4 on tb4.UNIT_CODE = t1.UNIT_CODE left join (select * from sys_dict where GROUP_NAME = 'REVIEW_SERIES') tb5 on tb5.DICT_CODE = t1.REVIEW_SERIES left join (select tab3.dict_code,tab3.dict_name as POSITIONAL_NAME,tab4.dict_name as level_name from sys_dict tab3 left join sys_dict tab4 on tab3.BACKUP2 = tab4.DICT_CODE where tab3.GROUP_NAME = 'POSITIONAL_TITLES') tb6 on tb6.DICT_CODE = t1.POSITIONAL_TITLES left join BASE_SERIES_PROFESSIAL tb7 on tb7.PROFESSIAL_CODE = t1.PROFESSIAL_CODE left join sys_dict tb8 on tb8.DICT_CODE = t1.REVIEW_TYPE and tb8.GROUP_NAME = 'REVIEW_TYPE' where t1.JUDGING_CODE in ( select JUDGING_CODE from base_judging where BACK3 = 1
and ID = #{judgingId}
and MANAGE_UNIT = #{manageUnitCode}
)
and t1.USER_NAME like '%'||#{userName}||'%'
and t1.YEAR_NO = #{yearNo}
and t1.REVIEW_SERIES = #{reviewSeries}
and t1.PROFESSIAL_CODE = #{professialCode}
and t1.ISPUBLICITY = #{ispublicity}
and t1.AREA_CODE IN (select AREA_CODE from SYS_AREA where AREA_CODE = #{areaCode} OR AREA_GRADE = '3' AND AREA_PARENTCODE = #{areaCode})
ORDER BY t1.YEAR_NO DESC ,t1.AREA_CODE,tb1.AREA_GRADE ,t1.OPT_TIME DESC )e
where rownum <=#{rows}*#{page}) where rn>(#{page}-1)*#{rows}

select count(*) as listcount from REC_REVIEWRESULTPUBLICITY  where JUDGING_CODE in
(
select JUDGING_CODE from base_judging where BACK3 = 1

  and ID = #{judgingId}


  and MANAGE_UNIT = #{manageUnitCode}

)

  and USER_NAME  like '%'||#{userName}||'%'


  and YEAR_NO = #{yearNo}


  and REVIEW_SERIES = #{reviewSeries}


  and PROFESSIAL_CODE = #{professialCode}


  and ISPUBLICITY = #{ispublicity}


  and AREA_CODE IN
  (select  AREA_CODE from SYS_AREA where AREA_CODE = #{areaCode} OR AREA_GRADE = '3' AND AREA_PARENTCODE = #{areaCode})
