mapper.select:
SELECT DISTINCT a1.*FROM
 sys_menu a1,
 (SELECT * FROM sys_menu WHERE menu_id IN (
       SELECT menu_id FROM sys_menufun t WHERE t.state='1' and t.menu_fun_id IN (
             SELECT t1.menu_fun_id FROM sys_rolemenufun t1 WHERE t1.role_id IN (
                   SELECT role_id FROM sys_userrole WHERE user_id =#{userId,jdbcType=VARCHAR}
                   )
            )
       )
 ) a2
 WHERE a1.menu_id = a2.menu_id OR a2.level_path  like concat(concat(('%'),(a1.menu_id)),('%'))
 ORDER BY a1.fsort,a1.menu_id DESC

