mapper.insert:

insert into sys_userrole (user_role_id, user_id, role_id
)
values (#{userRoleId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{roleId,jdbcType=VARCHAR}
)

mapper.update:

update sys_userrole
set user_id = #{userId,jdbcType=VARCHAR},
role_id = #{roleId,jdbcType=VARCHAR}
where user_role_id = #{userRoleId,jdbcType=VARCHAR}

mapper.select:
select role_id,user_role_id, user_id, unit_code, display_name, login_name, password, sex, mobilephone, email, id_card_no, address, add_user_id, add_time, state, user_themes, headportrait_img, backup1, backup2, backup3, UNIT_NAME from (select rownum rn,e.* from ( SELECT b.role_id, b.user_role_id, a.*, c.UNIT_NAME AS UNIT_NAME FROM sys_user a INNER JOIN sys_userrole b ON a.user_id = b.user_id INNER JOIN base_unit c ON a.unit_code = c.unit_code
where b.role_id = #{roleId}
ORDER by a.add_time) e
where rownum <=#{rows}*#{page}) b where rn>(#{page}-1)*#{rows}
